!(function (n, t, e) {
  console.log("init");
  var o = {
    init: function () {
      o.select2(),
        o.stellar(),
        o.portfolio(),
        o.lightCase(),
        o.sideNav(),
        o.aos(),
        o.navbarChange();
    },
    select2: function () {
      n(e).ready(function () {
        n("select").select2();
      });
    },
    stellar: function () {
      var e = n(t).width();
      767 <= e &&
        (n(".bg-fixed").attr("data-stellar-background-ratio", "0.8"),
        n.stellar({
          horizontalScrolling: !1,
          responsive: !0,
          parallaxBackgrounds: !0,
          scrollProperty: "scroll",
        }));
    },
    portfolio: function () {
      var t = n(".grid-portfolio").isotope({
        itemSelector: ".grid-item",
        masonry: {
          gutter: ".gutter-sizer",
          columnWidth: ".grid-sizer",
        },
        percentPosition: !0,
      });
      return (
        n(".filter-button-group").on("click", "a", function () {
          var e = n(this).attr("data-filter");
          t.isotope({
            filter: e,
          });
        }),
        n(".btn-filter a.is-checked").addClass("active"),
        n(".btn-filter a").on("click", function () {
          n(".btn-filter a").removeClass("active"), n(this).addClass("active");
        }),
        !1
      );
    },
    lightCase: function () {
      jQuery(e).ready(function (e) {
        e("a[data-rel^=lightcase]").lightcase();
      });
    },
    sideNav: function () {
      n("#side-nav-open").click(function () {
        n("#side-nav").css("width", "300"),
          setTimeout(function () {
            n("body").addClass("sidenav-open");
          }, 200),
          setTimeout(function () {
            n("body").addClass("in");
          }, 400);
      }),
        n("#side-nav-close, #canvas-overlay").click(function () {
          setTimeout(function () {
            n("body").removeClass("in");
          }, 200),
            setTimeout(function () {
              n("body").removeClass("sidenav-open"),
                n("#side-nav").css("width", "0");
            }, 400);
        }),
        n("#side-search-open").click(function () {
          n("#side-search").css("width", "300"),
            setTimeout(function () {
              n("body").addClass("sidesearch-open");
            }, 200),
            setTimeout(function () {
              n("body").addClass("in");
            }, 300);
        }),
        n("#side-search-close, #canvas-overlay").click(function () {
          setTimeout(function () {
            n("body").removeClass("in");
          }, 200),
            setTimeout(function () {
              n("body").removeClass("sidesearch-open"),
                n("#side-search").css("width", "0");
            }, 300);
        });
    },
    aos: function () {
      AOS.init({
        once: !0,
      });
    },
    navbarChange: function () {
      n(t).scroll(function () {
        var e = n(t).scrollTop();
        return (
          150 < e
            ? (n("#header-navbar").removeClass("navbar-transparent"),
              n("body").addClass("not-on-top"))
            : (n("body").removeClass("not-on-top"),
              n("#header-navbar").addClass("navbar-transparent")),
          !1
        );
      });
    },
  };
  n(e).ready(function () {
    o.init(n);
  });
})(window.jQuery, window, document);

$(window).scroll(function () {
  if ($(this).scrollTop() > 100) {
    $(".back-to-top").fadeIn("slow");
  } else {
    $(".back-to-top").fadeOut("slow");
  }
});

$(document).ready(function () {
  $(".back-to-top").on("click", function (event) {
    if (this.hash !== "") {
      event.preventDefault();
      var hash = this.hash;
      $("html, body").animate(
        {
          scrollTop: $(hash).offset().top,
        },
        1700,
        function () {
          window.location.hash = hash;
        }
      );
    }
  });
});
